/**
     * JavaScript library that allows you to create a vertical timeline of events.
     * 
     * @name vertical-timeline.js
     * @author EmaWebDesign
     * @version 1.0
     * @link https://github.com/emawebdesign/vertical-timeline.js
     * @license https://opensource.org/licenses/MIT MIT License
     */

class VerticalTimeline {
  constructor(params) {
    if (typeof params.id !== 'undefined' && typeof params.mode !== 'undefined' && typeof params.items !== 'undefined') {
      this.params = params;
      this.selector = document.getElementById(params.id);
      this.currentPage = 1;
      this.itemsPerPage = params.itemsPerPage || appsmith.model.itemsPerPage;
      this.init(params.mode);
    } else {
      throw new Error('Invalid parameters!');
    }
  }
  init(mode) {
    let modeClass = mode === "dark" ? 'dark-mode' : '';
    this.selector.innerHTML = `<div class="pagination-controls" style="text-align: right;">
										<button id="prevPage">Previous</button>
										<span class= "page-info" id="pageInfo"></span>
										<button id="nextPage">Next</button>
									</div>
									<div class="container-timeline ${modeClass}"></div>`;
    this.updateItems(this.params.items, true); // Initialize with initial items

    this.addPaginationEventListeners();
    this.updatePaginationControls();
  }
  updateItems(items, reset = false) {
    const timelineContainer = this.selector.querySelector('.container-timeline');
    if (reset) {
      timelineContainer.innerHTML = '<div class="timeline"></div>'; // Clear existing items if reset is true
    }
    const timeline = timelineContainer.querySelector('.timeline');
    if (items && items.length > 0) {
      const startIndex = (this.currentPage - 1) * this.itemsPerPage;
      const endIndex = Math.min(startIndex + this.itemsPerPage, items.length);
      for (let i = startIndex; i < endIndex; i++) {
        let item = items[i];
        let html = `<div class="timeline-container ${item.type}">`;
        html += `<div class="timeline-icon" data-index="${i}" data-record="${item.Id}">`;
        html += `<i class="${item.image}"></i>`;
        html += '</div>';
        html += '<div class="timeline-body">';
        html += `<h4 class="timeline-title"><span class="badge">${item.title}</span></h4>`;
        html += `<p>${item.text}</p>`;
        html += `<p class="timeline-subtitle">${item.user} - ${moment(item.date).format('DD/MM/YYYY')}</p>`;
        html += '</div>';
        html += '</div>';
        timeline.innerHTML += html;
      }
      this.addEventListeners();
    }
  }
  addEventListeners() {
    const icons = this.selector.querySelectorAll('.timeline-icon');
    icons.forEach(icon => {
      icon.addEventListener('click', event => {
        const index = event.currentTarget.getAttribute('data-index');
        const record = event.currentTarget.getAttribute('data-record');
        this.handleIconClick(index, record);
      });
    });
  }
  addPaginationEventListeners() {
    const prevButton = this.selector.querySelector('#prevPage');
    const nextButton = this.selector.querySelector('#nextPage');
    prevButton.addEventListener('click', () => {
      if (this.currentPage > 1) {
        this.currentPage--;
        this.updateItems(this.params.items, true);
        this.updatePaginationControls();
      }
    });
    nextButton.addEventListener('click', () => {
      const totalPages = Math.ceil(this.params.items.length / this.itemsPerPage);
      if (this.currentPage < totalPages) {
        this.currentPage++;
        this.updateItems(this.params.items, true);
        this.updatePaginationControls();
      }
    });
  }
  updatePaginationControls() {
    const pageInfo = this.selector.querySelector('#pageInfo');
    const totalPages = Math.ceil(this.params.items.length / this.itemsPerPage);
    pageInfo.textContent = `Page ${this.currentPage} of ${totalPages}`;
    const prevButton = this.selector.querySelector('#prevPage');
    const nextButton = this.selector.querySelector('#nextPage');
    prevButton.disabled = this.currentPage === 1;
    nextButton.disabled = this.currentPage === totalPages;
  }
  handleIconClick(index, record) {
    appsmith.triggerEvent("onClick", {
      itemIndex: index,
      Id: record
    });
    event.preventDefault();
  }
  update(newParams) {
    this.params.items = this.params.items.concat(newParams.items); // Append new items
    this.updateItems(newParams.items, false); // Add new items without resetting
    this.updatePaginationControls();
  }
}

//From here the Appsmith code
appsmith.onReady(() => {
  var timeline = new VerticalTimeline(appsmith.model);
});
const unlisten = appsmith.onModelChange(() => {
  var timeline = new VerticalTimeline(appsmith.model);
  timeline.update({
    items: model.items
  });
});
unlisten();

